var inst = mobiscroll.eventcalendar('#demo-mobile-month-view', {
    locale: mobiscroll.localeEs,
    theme: 'ios',
    themeVariant: 'light',
    clickToCreate: false,
    dragToCreate: false,
    dragToMove: false,
    dragToResize: false,
    eventDelete: false,
    view: {
      calendar: { type: 'month' },
      agenda: { type: 'month' },
    },
    onEventClick: function (args) {
      mobiscroll.toast({
        message: args.event.title,
      });
    },
  });
  
  mobiscroll.getJson(
    'https://trial.mobiscroll.com/events/?vers=5',
    function (events) {
      inst.setEvents(events);
    },
    'jsonp',
  );
    